sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
    //multiscan serial numbers and asset tags, and then iterate through them to create a separate database record for each

	var table = "x_562840_scanner_d_scans";  //custom mobile app db table

	for (i=0; i<input.add_inventory.length; i++){
		var grNew = new GlideRecord(table);   //create new db record reference
		//grNew.newRecord();
		grNew.serial_number = input.add_inventory[i]["SNI"];   //assign scanned value to db record field
		grNew.asset_tag = input.add_inventory[i]["Asset Tag"];
		//grNew.setValue("serial_number", input.add_asset[i]["Serial Number"]);
		grNew.insert();
    }

})(input);
